        <Fragment>

            <Formulario consultarApiLetras={consultarApiLetras}/>

          <div className="container mt-5">

            <div className="row">
                <div className="col-md-6">

                </div>

                <div className="col-md-6">
                      <Cancion  />
                </div>

            </div>

          </div>

        </Fragment>


    )

}

export default App;
